import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";

export default function RestaurantDetails({ id, goBack }) {
  const [info, setInfo] = useState(null);
  const { addToCart } = useCart();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => setInfo(r.data))
      .catch(() => setInfo(null));
  }, [id]);

  if (!info) return <div className="page-container">Loading…</div>;

  return (
    <div className="page-container" style={{ position: "relative", zIndex: 5 }}>
      {/* BACK BUTTON */}
      <button className="primary-btn" onClick={goBack}>
        ← Back
      </button>

      <h2 className="page-title">{info.name}</h2>
      <p className="card-sub">
        Cuisine: {info.cuisine} • ⭐ {info.avgRating}
      </p>

      <h3 style={{ marginTop: 12 }}>Menu</h3>

      {info.menu?.length ? (
        info.menu.map((m, i) => (
          <div
            key={i}
            style={{
              border: "1px solid #ddd",
              padding: "14px",
              marginBottom: "10px",
              borderRadius: "12px",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              cursor: "pointer",
              background: "white",
              zIndex: 10,
              pointerEvents: "auto",
            }}
            onClick={() =>
              addToCart({
                restaurantId: info._id,
                restaurantName: info.name,
                name: m.name,
                price: m.price,
              })
            }
          >
            <div>
              <div style={{ fontSize: 18, fontWeight: 600 }}>{m.name}</div>
              <div style={{ color: "#555" }}>₹ {m.price}</div>
            </div>

            <button
              className="primary-btn"
              style={{ zIndex: 20, pointerEvents: "auto" }}
              onClick={(e) => {
                e.stopPropagation();
                addToCart({
                  restaurantId: info._id,
                  restaurantName: info.name,
                  name: m.name,
                  price: m.price,
                });
              }}
            >
              + Add
            </button>
          </div>
        ))
      ) : (
        <p>No menu items.</p>
      )}
    </div>
  );
}
