import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";

export default function RestaurantDetails({ id, goBack, goToCart }) {
  const [info, setInfo] = useState(null);
  const { cart, addToCart } = useCart();

  // Visual feedback state
  const [justAdded, setJustAdded] = useState({});

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => setInfo(r.data))
      .catch(() => setInfo(null));
  }, [id]);

  if (!info) return <div className="page-container">Loading…</div>;

  function handleAdd(item) {
    addToCart(item);

    // show "Added" text for 1.2 sec
    setJustAdded((prev) => ({ ...prev, [item.name]: true }));
    setTimeout(() => {
      setJustAdded((prev) => {
        const copy = { ...prev };
        delete copy[item.name];
        return copy;
      });
    }, 1200);
  }

  // Calculate total & count for bottom-floating cart button
  const cartCount = cart.length;
  const cartTotal = cart.reduce((sum, i) => sum + i.price, 0);

  return (
    <div className="page-container" style={{ position: "relative" }}>
      <button className="primary-btn" onClick={goBack}>
        ← Back
      </button>

      <h2 className="page-title">{info.name}</h2>
      <p className="card-sub">
        Cuisine: {info.cuisine} • ⭐ {info.avgRating}
      </p>

      <h3 style={{ marginTop: 15 }}>Menu</h3>

      {info.menu?.length ? (
        info.menu.map((m, i) => {
          const item = {
            restaurantId: info._id,
            restaurantName: info.name,
            name: m.name,
            price: m.price,
          };

          return (
            <div key={i} className="menu-card">
              <div>
                <div className="menu-title">{m.name}</div>
                <div className="menu-price">₹ {m.price}</div>
              </div>

              <button
                className="primary-btn"
                type="button"
                onClick={() => handleAdd(item)}
              >
                {justAdded[m.name] ? "Added" : "+ Add"}
              </button>
            </div>
          );
        })
      ) : (
        <p>No menu items.</p>
      )}

      {/* FLOATING CART BAR (Only when cart has items) */}
      {cartCount > 0 && (
        <div className="floating-cart-bar" onClick={goToCart}>
          <strong>View Cart ({cartCount} items)</strong>
          <span>₹ {cartTotal}</span>
        </div>
      )}
    </div>
  );
}
