import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";

export default function RestaurantDetails({ id, goBack }) {
  const [info, setInfo] = useState(null);
  const { addToCart } = useCart();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => setInfo(r.data))
      .catch(() => setInfo(null));
  }, [id]);

  if (!info) return <div className="page-container">Loading…</div>;

  return (
    <div className="page-container">

      {/* Back */}
      <button className="primary-btn" onClick={goBack}>
        ← Back
      </button>

      {/* Title */}
      <h2 className="page-title">{info.name}</h2>
      <p className="card-sub">
        Cuisine: {info.cuisine} • ⭐ {info.avgRating}
      </p>

      <h3 style={{ marginTop: 15 }}>Menu</h3>

      {info.menu?.length ? (
        info.menu.map((m, i) => (
          <div
            key={i}
            className="menu-card"
            onClick={() =>
              addToCart({
                restaurantId: info._id,
                restaurantName: info.name,
                name: m.name,
                price: m.price,
              })
            }
          >
            {/* LEFT: Title & Price */}
            <div className="menu-left">
              <div className="menu-title">{m.name}</div>
              <div className="menu-price">₹ {m.price}</div>
            </div>

            {/* RIGHT: Add button */}
            <button
              className="primary-btn"
              onClick={(e) => {
                e.stopPropagation();
                addToCart({
                  restaurantId: info._id,
                  restaurantName: info.name,
                  name: m.name,
                  price: m.price,
                });
              }}
            >
              + Add
            </button>
          </div>
        ))
      ) : (
        <p>No menu items.</p>
      )}
    </div>
  );
}
