import React from "react";
import { useCart } from "../context/CartContext";
import axios from "axios";

export default function Cart({ token }) {
  const { cart, clearCart } = useCart();

  if (!cart.length)
    return <h3 style={{ textAlign: "center" }}>Your cart is empty</h3>;

  const total = cart.reduce((a, b) => a + b.price, 0);

  async function placeOrder() {
    try {
      const res = await axios.post(
        "http://localhost:5000/api/orders/place",
        { userId: "demoUser", items: cart },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("Order placed!");
      clearCart();
    } catch (e) {
      alert("Order failed");
    }
  }

  return (
    <div className="page-container">
      <h2>Your Cart</h2>

      {cart.map((item, i) => (
        <div key={i} className="card">
          <div className="card-title">{item.name}</div>
          <div className="card-sub">₹ {item.price}</div>
        </div>
      ))}

      <h3 style={{ marginTop: 10 }}>Total: ₹ {total}</h3>

      <button className="primary-btn" onClick={placeOrder}>
        Place Order
      </button>
    </div>
  );
}
