import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";
import FloatingCartBar from "./FloatingCartBar";

export default function RestaurantDetails({ id, goBack }) {
  const [info, setInfo] = useState(null);
  const [stars, setStars] = useState(5);
  const [comment, setComment] = useState("");

  const { cart, addToCart, incQty, decQty, removeFromCart } = useCart();

  function loadRest() {
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => setInfo(r.data))
      .catch(() => setInfo(null));
  }

  useEffect(() => {
    loadRest();
  }, [id]);

  if (!info) return <div className="page-container"><p>Loading…</p></div>;

  function submitRating() {
    axios.post(`http://localhost:5000/api/restaurant/${id}/rate`, {
      userId: "demoUser",
      stars,
      comment
    }).then(() => {
      alert("Thanks for your feedback!");
      setComment("");
      setStars(5);
      loadRest();
    });
  }

  function cartEntryFor(item) {
    return cart.find(c => c.restaurantId === info._id && c.name === item.name);
  }

  function handleAdd(item) {
    addToCart({
      restaurantId: info._id,
      restaurantName: info.name,
      name: item.name,
      price: item.price
    });
  }

  return (
    <div className="page-container">

      <button className="primary-btn back-btn" onClick={goBack}>
        ← Back
      </button>

      <h2 className="page-title">{info.name}</h2>
      <p className="rest-sub-info">
        Cuisine: {info.cuisine} • ⭐ {info.avgRating?.toFixed(1)}
      </p>

      {/* MENU SECTION ----------------------- */}
      <h3 className="menu-heading">Menu</h3>

      {info.menu.map((m, i) => {
        const ent = cartEntryFor(m);

        return (
          <div key={i} className="menu-card">

            <div className="menu-left">
              <div className="menu-title">{m.name}</div>
              <div className="menu-price">₹ {m.price}</div>
            </div>

            {!ent ? (
              <button className="menu-add-btn" onClick={() => handleAdd(m)}>
                + Add
              </button>
            ) : (
              <div style={{ display: "flex", gap: 8 }}>
                <button className="qty-btn"
                  onClick={() => decQty({ restaurantId: info._id, name: m.name })}>−</button>

                <div className="qty-number">{ent.qty}</div>

                <button className="qty-btn"
                  onClick={() => incQty({ restaurantId: info._id, name: m.name })}>+</button>

                <button className="remove-btn"
                  onClick={() => removeFromCart({ restaurantId: info._id, name: m.name })}>
                  Remove
                </button>
              </div>
            )}
          </div>
        );
      })}

      {/* RATING SECTION --------------------------- */}
      <h3 className="menu-heading">Rate This Restaurant</h3>

      <select value={stars} onChange={(e) => setStars(Number(e.target.value))}
        style={{ padding: 8, marginBottom: 10 }}>
        <option value={5}>5 ★</option>
        <option value={4}>4 ★</option>
        <option value={3}>3 ★</option>
        <option value={2}>2 ★</option>
        <option value={1}>1 ★</option>
      </select>

      <br />

      <textarea
        placeholder="Add a comment (optional)"
        value={comment}
        onChange={(e) => setComment(e.target.value)}
        style={{
          width: "90%",
          height: 70,
          marginTop: 8,
          padding: 10,
          borderRadius: 8,
          border: "1px solid #ccc"
        }}
      ></textarea>

      <br />

      <button className="primary-btn" onClick={submitRating}>Submit Rating</button>

      <FloatingCartBar />
    </div>
  );
}
