// src/components/SuccessAnimation.js
import React, { useEffect } from "react";

export default function SuccessAnimation({ open, onFinish, message="Donation Assigned ✓" }) {
  useEffect(() => {
    if (!open) return;
    const t = setTimeout(() => {
      if (onFinish) onFinish();
    }, 1500);
    return () => clearTimeout(t);
  }, [open, onFinish]);

  if (!open) return null;
  return (
    <div style={styles.overlay}>
      <div style={styles.box}>
        <div style={styles.check}>✓</div>
        <div style={{ marginTop: 8, fontWeight: 600 }}>{message}</div>
      </div>
    </div>
  );
}

const styles = {
  overlay: { position:"fixed", left:0, top:0, right:0, bottom:0, display:"flex", alignItems:"center", justifyContent:"center", zIndex:1400 },
  box: { background:"#fff", padding:24, borderRadius:12, boxShadow:"0 12px 30px rgba(0,0,0,0.12)", textAlign:"center" },
  check: { width:72, height:72, borderRadius:36, background:"#22c55e", color:"#fff", display:"flex", alignItems:"center", justifyContent:"center", fontSize:36, margin: "0 auto" }
};
