// frontend/src/components/RestaurantDetails.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";
import FloatingCartBar from "./FloatingCartBar";

export default function RestaurantDetails({ id, goBack }) {
  const [info, setInfo] = useState(null);
  const { addToCart } = useCart();
  const [justAdded, setJustAdded] = useState(null);

  // Fetch restaurant info
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => setInfo(r.data))
      .catch(() => setInfo(null));
  }, [id]);

  if (!info)
    return (
      <div className="page-container">
        <p>Loading…</p>
      </div>
    );

  // Handle add to cart
  function handleAdd(item) {
    addToCart({
      restaurantId: info._id,
      restaurantName: info.name,
      name: item.name,
      price: item.price,
    });

    // show “added” message temporarily
    setJustAdded(item.name);
    setTimeout(() => setJustAdded(null), 1500);
  }

  return (
    <div className="page-container">
      {/* Back Button */}
      <button className="primary-btn" onClick={goBack}>
        ← Back
      </button>

      {/* Restaurant Header */}
      <h2 className="page-title" style={{ marginTop: 10 }}>
        {info.name}
      </h2>

      <p className="small-muted" style={{ marginBottom: 20 }}>
        Cuisine: {info.cuisine} &nbsp; • &nbsp; ⭐ {info.avgRating}
      </p>

      <h3 style={{ marginTop: 12, marginBottom: 10 }}>Menu</h3>

      {/* MENU ITEMS */}
      {info.menu?.length > 0 ? (
        info.menu.map((m, idx) => (
          <div
            key={idx}
            className="menu-card"
            onClick={() => handleAdd(m)} // whole row clickable
          >
            <div className="menu-left">
              <div className="menu-title">{m.name}</div>
              <div className="menu-price">₹ {m.price}</div>
            </div>

            {/* Add Button */}
            <button
              className="menu-add-btn"
              onClick={(e) => {
                e.stopPropagation(); // prevent double-add
                handleAdd(m);
              }}
            >
              + Add
            </button>
          </div>
        ))
      ) : (
        <p>No menu items.</p>
      )}

      {/* Inline confirmation */}
      {justAdded && (
        <div className="inline-msg" style={{ marginTop: 12 }}>
          {justAdded} added to cart ✔
        </div>
      )}
      <FloatingCartBar onViewCart={() => goBack("cart")} />
    </div>
  );
}
