// frontend/src/context/CartContext.js
import React, { createContext, useContext, useState } from "react";

const CartContext = createContext();
export const useCart = () => useContext(CartContext);

export function CartProvider({ children }) {
  const [cart, setCart] = useState([]);

  // Add item OR increase qty
  function addToCart(item) {
    const exists = cart.find((c) => c.name === item.name);

    if (exists) {
      setCart(
        cart.map((c) =>
          c.name === item.name ? { ...c, qty: c.qty + 1 } : c
        )
      );
    } else {
      setCart([...cart, { ...item, qty: 1 }]);
    }
  }

  // Decrease quantity or remove item fully
  function removeFromCart(itemName) {
    const exists = cart.find((c) => c.name === itemName);

    if (!exists) return;

    if (exists.qty === 1) {
      // completely remove
      setCart(cart.filter((c) => c.name !== itemName));
    } else {
      // decrease quantity
      setCart(
        cart.map((c) =>
          c.name === itemName ? { ...c, qty: c.qty - 1 } : c
        )
      );
    }
  }

  // Remove completely
  function removeItemCompletely(itemName) {
    setCart(cart.filter((c) => c.name !== itemName));
  }

  function clearCart() {
    setCart([]);
  }

  return (
    <CartContext.Provider
      value={{
        cart,
        addToCart,
        removeFromCart,
        removeItemCompletely,
        clearCart,
      }}
    >
      {children}
    </CartContext.Provider>
  );
}
