import React, { useState } from "react";
import axios from "axios";
import RestaurantDetails from "./RestaurantDetails";
import "../styles.css";

export default function FindFood({ token }) {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [loading, setLoading] = useState(false);

  async function fetchLocation() {
    setLoading(true);

    navigator.geolocation.getCurrentPosition(async (pos) => {
      try {
        const res = await axios.get("http://localhost:5000/api/recommend", {
          params: {
            lat: pos.coords.latitude,
            lng: pos.coords.longitude,
          },
          headers: token ? { Authorization: `Bearer ${token}` } : {},
        });

        setRestaurants(res.data || []);
      } catch (err) {
        console.log("Recommend error:", err);
      }
      setLoading(false);
    });
  }

  // ‚¨áÔ∏è VERY IMPORTANT ‚Äî pass goToCart() so floating cart bar works
  if (selectedId) {
    return (
      <RestaurantDetails
        id={selectedId}
        goBack={() => setSelectedId(null)}
        goToCart={() => window.goToCart()}   // THIS FIXES CLICK ISSUE
      />
    );
  }

  return (
    <div className="page-container">
      <h1 className="page-title">Find Food Near You üçΩÔ∏è</h1>

      <button className="primary-btn" onClick={fetchLocation}>
        {loading ? "Finding..." : "Use My Location"}
      </button>

      <div style={{ marginTop: 20 }}>
        {restaurants.map((r) => {
          const id = r.restaurant?._id || r._id;
          return (
            <div
              key={id}
              className="card"
              style={{ cursor: "pointer" }}
              onClick={() => setSelectedId(id)}
            >
              <div className="card-title">{r.restaurant?.name || r.name}</div>
              <div className="card-sub">
                Cuisine: {r.restaurant?.cuisine || r.cuisine}
              </div>
              <div className="card-sub">
                Rating: ‚≠ê {r.restaurant?.avgRating || r.avgRating}
              </div>
              <div className="card-sub">
                Distance: {(r.distanceKm || 0).toFixed(2)} km
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
