import React from "react";
import { useCart } from "../context/CartContext";

export default function FloatingCartBar({ onViewCart }) {
  const { cart } = useCart();

  if (!cart.length) return null; // Only show if cart has items

  const total = cart.reduce((sum, item) => sum + item.price, 0);

  return (
    <div
      style={{
        position: "fixed",
        bottom: 20,
        left: "50%",
        transform: "translateX(-50%)",
        background: "#6c63ff",
        padding: "14px 22px",
        borderRadius: "50px",
        color: "white",
        boxShadow: "0 6px 18px rgba(0,0,0,0.2)",
        cursor: "pointer",
        fontSize: "16px",
        zIndex: 999,
        display: "flex",
        alignItems: "center",
        gap: "12px",
      }}
      onClick={onViewCart}
    >
      ðŸ›’ View Cart ({cart.length})
      <span
        style={{
          background: "white",
          color: "#6c63ff",
          padding: "4px 10px",
          borderRadius: "20px",
          fontWeight: 600,
        }}
      >
        â‚¹ {total}
      </span>
    </div>
  );
}
