// src/components/NGOLogin.js
import React, { useState } from "react";
import axios from "axios";

export default function NGOLogin({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  async function submit(e) {
    e.preventDefault();
    setMsg("");
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/ngo/auth/login", { email, password });
      const { token, ngo } = res.data;
      // pass token + ngo to parent
      onLogin({ token, ngo });
    } catch (err) {
      console.error(err);
      setMsg(err?.response?.data?.message || "Login failed");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="page-container">
      <div style={{ maxWidth: 420, margin: "0 auto" }}>
        <h2>NGO Login</h2>
        {msg && <div className="alert">{msg}</div>}
        <form className="card" onSubmit={submit} style={{ display: "flex", gap: 10, flexDirection: "column" }}>
          <label>Email</label>
          <input className="input" value={email} onChange={e=>setEmail(e.target.value)} />
          <label>Password</label>
          <input type="password" className="input" value={password} onChange={e=>setPassword(e.target.value)} />
          <button className="primary-btn" disabled={loading}>{loading ? "Signing in…" : "Sign in"}</button>
        </form>
      </div>
    </div>
  );
}
