// frontend/src/components/RestaurantDetails.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";
import FloatingCartBar from "./FloatingCartBar";

export default function RestaurantDetails({ id, goBack, setPage }) {
  const [info, setInfo] = useState(null);
  const { cart, addToCart, removeFromCart } = useCart();

  // Fetch restaurant info
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => setInfo(r.data))
      .catch(() => setInfo(null));
  }, [id]);

  if (!info) return <div className="page-container">Loading…</div>;

  // Helper: get qty of item
  function getQty(name) {
    return cart.find((x) => x.name === name)?.qty || 0;
  }

  return (
    <div className="page-container">
      <button className="primary-btn" onClick={goBack}>
        ← Back
      </button>

      <h2 className="page-title">{info.name}</h2>
      <p className="small-muted">
        Cuisine: {info.cuisine} • ⭐ {info.avgRating}
      </p>

      <h3 style={{ marginTop: 12 }}>Menu</h3>

      {info.menu.map((m, i) => {
        const qty = getQty(m.name);

        return (
          <div key={i} className="menu-card">
            <div>
              <div className="menu-title">{m.name}</div>
              <div className="menu-price">₹ {m.price}</div>
            </div>

            {/* Right section */}
            {qty === 0 ? (
              <button
                className="menu-add-btn"
                onClick={() =>
                  addToCart({
                    restaurantId: info._id,
                    restaurantName: info.name,
                    name: m.name,
                    price: m.price,
                  })
                }
              >
                + Add
              </button>
            ) : (
              <div className="qty-box">
                <button
                  className="qty-btn"
                  onClick={() => removeFromCart(m.name)}
                >
                  –
                </button>

                <span className="qty-number">{qty}</span>

                <button
                  className="qty-btn"
                  onClick={() =>
                    addToCart({
                      restaurantId: info._id,
                      restaurantName: info.name,
                      name: m.name,
                      price: m.price,
                    })
                  }
                >
                  +
                </button>
              </div>
            )}
          </div>
        );
      })}

      <FloatingCartBar setPage={setPage} />
    </div>
  );
}
