import React from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";

export default function Cart({ token, goBack }) {
  const { cart, addToCart, removeFromCart, removeItemCompletely, clearCart } =
    useCart();

  if (!cart.length)
    return (
      <div className="page-container">
        <h3>Your cart is empty</h3>
        <button className="primary-btn" onClick={goBack}>
          ← Back
        </button>
      </div>
    );

  const total = cart.reduce((sum, i) => sum + i.price * i.qty, 0);

  async function placeOrder() {
    try {
      await axios.post(
        "http://localhost:5000/api/orders/place",
        { userId: "demoUser", items: cart },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("Order placed!");
      clearCart();
    } catch (e) {
      alert("Order failed");
    }
  }

  return (
    <div className="page-container">
      <h2>Your Cart</h2>

      {cart.map((item, i) => (
        <div key={i} className="menu-card" style={{ justifyContent: "space-between" }}>
          <div>
            <div className="menu-title">{item.name}</div>
            <div className="menu-price">₹ {item.price}</div>
          </div>

          {/* Quantity Control */}
          <div className="qty-box">
            <button className="qty-btn" onClick={() => removeFromCart(item.name)}>
              −
            </button>
            <span className="qty-num">{item.qty}</span>
            <button className="qty-btn" onClick={() => addToCart(item)}>
              +
            </button>
          </div>

          <button
            className="remove-btn"
            onClick={() => removeItemCompletely(item.name)}
          >
            Remove
          </button>
        </div>
      ))}

      <h3 style={{ marginTop: 12 }}>Total: ₹ {total}</h3>

      <button className="primary-btn" onClick={placeOrder}>
        Place Order
      </button>
    </div>
  );
}
