import React, { createContext, useContext, useEffect, useState, useCallback } from "react";

const CartContext = createContext();
export const useCart = () => useContext(CartContext);

export function CartProvider({ children }) {
  // initialize from localStorage if present
  const [cart, setCart] = useState(() => {
    try {
      const raw = localStorage.getItem("tastebuds_cart");
      return raw ? JSON.parse(raw) : [];
    } catch {
      return [];
    }
  });

  // persist cart to localStorage whenever it changes
  useEffect(() => {
    try {
      localStorage.setItem("tastebuds_cart", JSON.stringify(cart));
    } catch (e) {
      console.warn("Failed to persist cart", e);
    }
  }, [cart]);

  // adds an item (simple working cart). Use callback to avoid stale closures
  const addToCart = useCallback((item) => {
    setCart(prev => {
      const newCart = [...prev, item];
      console.log("Cart updated:", newCart); // visible in DevTools
      return newCart;
    });
  }, []);

  const clearCart = useCallback(() => {
    setCart([]);
    try {
      localStorage.removeItem("tastebuds_cart");
    } catch {}
  }, []);

  return (
    <CartContext.Provider value={{ cart, addToCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}
