import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";
import FloatingCartBar from "./FloatingCartBar";

export default function RestaurantDetails({ id, goBack, openCart }) {
  const [info, setInfo] = useState(null);
  const { addToCart, cart } = useCart();
  const [justAdded, setJustAdded] = useState(null);

  // Fetch restaurant info
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => setInfo(r.data))
      .catch(() => setInfo(null));
  }, [id]);

  if (!info)
    return (
      <div className="page-container">
        <p>Loading…</p>
      </div>
    );

  // ADD TO CART
  function handleAdd(item) {
    addToCart({
      restaurantId: info._id,
      restaurantName: info.name,
      name: item.name,
      price: item.price,
    });

    setJustAdded(item.name);
    setTimeout(() => setJustAdded(null), 1500);
  }

  return (
    <div className="page-container restaurant-details">

      {/* BACK BUTTON */}
      <button className="primary-btn back-btn" onClick={goBack}>
        ← Back
      </button>

      {/* RESTAURANT HEADER */}
      <h2 className="page-title">{info.name}</h2>

      <p className="rest-sub-info">
        Cuisine: {info.cuisine} &nbsp; • &nbsp; ⭐ {info.avgRating}
      </p>

      <h3 className="menu-heading">Menu</h3>

      {/* MENU ITEMS */}
      {info.menu?.length > 0 ? (
        info.menu.map((m, idx) => (
          <div
            key={idx}
            className="menu-card"
            onClick={() => handleAdd(m)}
          >
            <div className="menu-left">
              <div className="menu-title">{m.name}</div>
              <div className="menu-price">₹ {m.price}</div>
            </div>

            <button
              className="menu-add-btn"
              onClick={(e) => {
                e.stopPropagation();
                handleAdd(m);
              }}
            >
              + Add
            </button>
          </div>
        ))
      ) : (
        <p>No menu items.</p>
      )}

      {/* INLINE ADDED MESSAGE */}
      {justAdded && (
        <div className="inline-msg">{justAdded} added ✔</div>
      )}

      {/* FLOATING VIEW CART BAR */}
      {cart.length > 0 && (
        <FloatingCartBar onViewCart={openCart} />
      )}
    </div>
  );
}
