// src/components/RestaurantDetails.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";
import FloatingCartBar from "./FloatingCartBar";

export default function RestaurantDetails({ id, goBack }) {
  const [info, setInfo] = useState(null);
  const { cart, addToCart, incQty, decQty, removeFromCart } = useCart();
  const [justAdded, setJustAdded] = useState(null);

  // ‚≠ê NEW STATE FOR RATING
  const [showRating, setShowRating] = useState(false);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");
  const [ratingLoading, setRatingLoading] = useState(false);

  // Fetch restaurant details
  useEffect(() => {
    setInfo(null);
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => setInfo(r.data))
      .catch((e) => {
        console.error("restaurant fetch error:", e);
        setInfo(null);
      });
  }, [id]);

  if (!info)
    return (
      <div className="page-container">
        <p>Loading‚Ä¶</p>
      </div>
    );

  // helper: get cart item
  function cartEntryFor(item) {
    return cart.find(
      (c) => c.restaurantId === info._id && c.name === item.name
    );
  }

  function handleAdd(item, e) {
    if (e) e.stopPropagation();
    addToCart({
      restaurantId: info._id,
      restaurantName: info.name,
      name: item.name,
      price: item.price,
    });
    setJustAdded(item.name);
    setTimeout(() => setJustAdded(null), 1400);
  }

  // ‚≠ê SUBMIT RATING FUNCTION
  const submitRating = async () => {
    try {
      setRatingLoading(true);

      const token = localStorage.getItem("token");

      const res = await fetch(
        `http://localhost:5000/api/restaurant/${info._id}/rate`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: token ? `Bearer ${token}` : "",
          },
          body: JSON.stringify({ rating, comment }),
        }
      );

      const data = await res.json();

      if (!res.ok) {
        alert(data.message || "Rating failed");
        setRatingLoading(false);
        return;
      }

      alert("Thank you! Your rating has been submitted üéâ");

      // Update UI rating immediately
      setInfo(data.restaurant);

      setShowRating(false);
      setComment("");
      setRating(5);
    } catch (err) {
      console.error(err);
      alert("Something went wrong submitting rating.");
    } finally {
      setRatingLoading(false);
    }
  };

  return (
    <div className="page-container">
      <button className="primary-btn back-btn" onClick={() => goBack && goBack()}>
        ‚Üê Back
      </button>

      <h2 className="page-title">{info.name}</h2>
      <p className="rest-sub-info">
        Cuisine: {info.cuisine} &nbsp; ‚Ä¢ &nbsp; ‚≠ê {info.avgRating.toFixed(1)}
      </p>

      {/* ‚≠ê RATE THIS RESTAURANT BUTTON */}
      <button
        className="primary-btn"
        style={{ marginBottom: "20px", padding: "8px 14px" }}
        onClick={() => setShowRating(true)}
      >
        ‚≠ê Rate this Restaurant
      </button>

      <h3 className="menu-heading">Menu</h3>

      {info.menu && info.menu.length > 0 ? (
        info.menu.map((m, idx) => {
          const ent = cartEntryFor(m);
          return (
            <div key={idx} className="menu-card" style={{ cursor: "default" }}>
              <div className="menu-left">
                <div className="menu-title">{m.name}</div>
                <div className="menu-price">‚Çπ {m.price}</div>
              </div>

              <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
                {!ent ? (
                  <button
                    className="menu-add-btn"
                    onClick={(e) => handleAdd(m, e)}
                    aria-label={`Add ${m.name}`}
                  >
                    + Add
                  </button>
                ) : (
                  <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
                    <button
                      className="qty-btn"
                      onClick={() =>
                        decQty({
                          restaurantId: info._id,
                          name: m.name,
                        })
                      }
                      aria-label={`Decrease ${m.name}`}
                    >
                      ‚àí
                    </button>

                    <div className="qty-number">{ent.qty}</div>

                    <button
                      className="qty-btn"
                      onClick={() =>
                        incQty({
                          restaurantId: info._id,
                          name: m.name,
                        })
                      }
                      aria-label={`Increase ${m.name}`}
                    >
                      +
                    </button>

                    <button
                      className="remove-btn"
                      onClick={() =>
                        removeFromCart({
                          restaurantId: info._id,
                          name: m.name,
                        })
                      }
                      aria-label={`Remove ${m.name}`}
                    >
                      Remove
                    </button>
                  </div>
                )}
              </div>
            </div>
          );
        })
      ) : (
        <p>No menu items.</p>
      )}

      {justAdded && <div className="inline-msg">{justAdded} added ‚úî</div>}

      {/* Floating cart bar */}
      <FloatingCartBar />

      {/* ‚≠ê RATING MODAL */}
      {showRating && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            style={{
              background: "white",
              padding: "20px",
              width: "320px",
              borderRadius: "10px",
              boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
            }}
          >
            <h3 style={{ marginBottom: "10px" }}>Rate {info.name}</h3>

            <label>Rating (1‚Äì5)</label>
            <select
              value={rating}
              onChange={(e) => setRating(Number(e.target.value))}
              style={{ width: "100%", padding: "6px", marginTop: "5px" }}
            >
              {[5, 4, 3, 2, 1].map((n) => (
                <option key={n} value={n}>
                  {n} ‚òÖ
                </option>
              ))}
            </select>

            <label style={{ marginTop: "10px", display: "block" }}>
              Comment
            </label>
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              style={{ width: "100%", height: "60px", marginTop: "5px" }}
            />

            <div style={{ marginTop: "15px", display: "flex", gap: "10px" }}>
              <button
                onClick={submitRating}
                disabled={ratingLoading}
                style={{
                  background: "#6b46c1",
                  color: "white",
                  padding: "8px 12px",
                  border: "none",
                  borderRadius: "6px",
                  flex: 1,
                }}
              >
                {ratingLoading ? "Submitting‚Ä¶" : "Submit"}
              </button>

              <button
                onClick={() => setShowRating(false)}
                style={{
                  padding: "8px 12px",
                  background: "#ddd",
                  borderRadius: "6px",
                  flex: 1,
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
