import React, { useEffect, useState } from "react";
import axios from "axios";
import { useCart } from "../context/CartContext";

export default function RestaurantDetails({ id, goBack }) {
  const [info, setInfo] = useState(null);
  const { addToCart } = useCart();

  // keep a small map of which menu items were recently added so we can show "Added"
  const [justAdded, setJustAdded] = useState({});

  useEffect(() => {
    let cancelled = false;
    axios
      .get(`http://localhost:5000/api/restaurant/${id}`)
      .then((r) => {
        if (!cancelled) setInfo(r.data);
      })
      .catch((err) => {
        console.error("Fetch restaurant error:", err);
        if (!cancelled) setInfo(null);
      });
    return () => (cancelled = true);
  }, [id]);

  if (!info) return <div className="page-container">Loading…</div>;

  // helper to add and show temporary feedback
  function handleAdd(item) {
    // call context addToCart
    addToCart(item);

    // show "Added" for this item for 1.2s
    setJustAdded(prev => ({ ...prev, [item.name]: true }));
    setTimeout(() => {
      setJustAdded(prev => {
        const copy = { ...prev };
        delete copy[item.name];
        return copy;
      });
    }, 1200);
  }

  return (
    <div className="page-container">
      <button className="primary-btn" onClick={goBack} type="button">
        ← Back
      </button>

      <h2 className="page-title">{info.name}</h2>
      <p className="card-sub">
        Cuisine: {info.cuisine} • ⭐ {info.avgRating}
      </p>

      <h3 style={{ marginTop: 15 }}>Menu</h3>

      {info.menu && info.menu.length ? (
        info.menu.map((m, i) => {
          const item = {
            restaurantId: info._id,
            restaurantName: info.name,
            name: m.name,
            price: m.price,
          };

          return (
            <div
              key={i}
              className="menu-card"
              onClick={() => handleAdd(item)} // clicking the row also adds
            >
              <div className="menu-left">
                <div className="menu-title">{m.name}</div>
                <div className="menu-price">₹ {m.price}</div>
              </div>

              <button
                type="button"                      // important: prevents accidental form submit
                className="primary-btn"
                onClick={(e) => {
                  e.stopPropagation();            // prevent the parent onClick from firing twice
                  handleAdd(item);
                }}
              >
                {justAdded[m.name] ? "Added" : "+ Add"}
              </button>
            </div>
          );
        })
      ) : (
        <p>No menu items.</p>
      )}
    </div>
  );
}
