import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Orders({ token }) {
  const [orders, setOrders] = useState([]);
  const userId = localStorage.getItem("userId") || "guest";

  useEffect(()=> {
    axios.get(`http://localhost:5000/api/orders/${userId}`)
      .then(r=> setOrders(r.data))
      .catch(e=> console.error(e));
  }, []);

  return (
    <div className="page-container">
      <h1 className="page-title">My Orders</h1>
      {orders.length===0 ? <p>No orders yet.</p> : orders.map(o=>(
        <div key={o._id} className="card">
          <div className="card-title">Order #{o._id}</div>
          <div className="card-sub">Total: â‚¹{o.total}</div>
          <div className="card-sub">Items: {o.items?.length || 0}</div>
          <div className="card-sub">Status: {o.status || "placed"}</div>
        </div>
      ))}
    </div>
  );
}
